      out.write("            vorname: user.vorname || '',\n");
      out.write("            abteilung: user.abteilung || '',\n");
      out.write("            effort_days: user.effort_days || 0\n");
      out.write("        }));\n");
      out.write("        \n");
      out.write("        console.log('Geladene Zuweisungen:', assignedUsers);\n");
      out.write("        updateAssignedUsersDisplay();\n");
      out.write("        \n");
      out.write("        \n");
      out.write("    } catch (error) {\n");
      out.write("        console.error('Fehler beim Laden der zugewiesenen Benutzer:', error);\n");
