      out.write("    }\n");
      out.write("}\n");
      out.write("\n");
      out.write("function showUserSelectDialog() {\n");
      out.write("    document.getElementById('userSelectDialog').showModal();\n");
      out.write("}\n");
      out.write("\n");
      out.write("function closeUserSelectDialog() {\n");
