      out.write("    try {\n");
      out.write("        const select = document.getElementById('userSelect');\n");
      out.write("        const userId = select.value;\n");
      out.write("        \n");
      out.write("        if (!userId) {\n");
      out.write("            throw new Error('Bitte wählen Sie einen Benutzer aus');\n");
      out.write("        }\n");
      out.write("        \n");
      out.write("        // Prüfen ob der Benutzer bereits zugewiesen ist\n");
      out.write("        if (assignedUsers.some(u => u.id.toString() === userId.toString())) {\n");
      out.write("            alert('Dieser Benutzer ist bereits zugewiesen');\n");
      out.write("            closeUserSelectDialog();\n");
      out.write("            return;\n");
      out.write("        }\n");
      out.write("        \n");
      out.write("        // Füge den neuen Benutzer zur bestehenden Liste hinzu\n");
      out.write("        const selectedOption = select.options[select.selectedIndex];\n");
      out.write("        const [name, vorname] = selectedOption.text.split(', ');\n");
      out.write("        const abteilung = selectedOption.text.match(/\\((.*?)\\)/)?.[1] || '';\n");
